targets.queryText:
query ($from: Long!, $to: Long!, $fragmentId: Int!, $identifier: String!) {
  data: queryStatusIdentifier(
    from: $from
    to: $to
    fragmentId: $fragmentId
    identifier: $identifier
  ) {
    roverStatus {
      dateMillis
      packet {
        batteryVoltage
        identityInfo {
          displayName
        }
      }
    }
  }
}

